declare(strict_types=1);

namespace Mageplaza\ShopbybrandGraphQl\Model\Resolver\Brands;

use Magento\Framework\Exception\LocalizedException;
use Magento\Framework\GraphQl\Config\Element\Field;
use Magento\Framework\GraphQl\Query\ResolverInterface;
use Magento\Framework\GraphQl\Schema\Type\ResolveInfo;
use Mageplaza\Shopbybrand\Helper\Data;
use Mageplaza\Shopbybrand\Model\Brand;

/**
 * Class BrandImage
 * @package Mageplaza\ShopbybrandGraphQl\Model\Resolver\Brands
 */
class BrandImage implements ResolverInterface
{
    /**
     * @var Data
     */
    protected $helperData;

    /**
     * BrandImage constructor.
     *
     * @param Data $helperData
     */
    public function __construct(Data $helperData)
    {
        $this->helperData = $helperData;
    }

    /**
     * @inheritdoc
     */
    public function resolve(Field $field, $context, ResolveInfo $info, array $value = null, array $args = null)
    {
        if (!isset($value['model'])) {
            throw new LocalizedException(__('"model" value should be specified'));
        }

        /** @var Brand $brand */
        $brand = $value['model'];

        return $this->helperData->getBrandImageUrl($brand);
    }
}
